css:
:root {
  font-size: 16px;
}
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: rgba(171, 178, 191, 0.8);
  background-color: transparent;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .input-checkbox {
  margin-top: -0.2em;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: rgba(171, 178, 191, 0.6);
  transition: color 0.2s;
}
.about-version-container:hover {
  color: rgba(214, 218, 224, 0.8);
}
.about-version-container:active {
  color: rgba(255, 255, 255, 0.8);
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 0.5rem;
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 0.25rem;
  background-color: rgba(0, 150, 136, 0.1);
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: rgba(171, 178, 191, 0.5);
}
.about-updates-label.is-strong {
  color: rgba(171, 178, 191, 0.8);
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-love .icon::before {
  position: relative;
  width: auto;
  height: auto;
  margin-right: 0;
  font-size: 1.5em;
  vertical-align: text-top;
}
.about-love .icon-logo-github::before {
  font-size: 3.6em;
  height: .36em;
}
.about-credits {
  color: rgba(171, 178, 191, 0.5);
}
.about-release-notes {
  color: #9acffa;
}
.about-release-notes:hover {
  color: #e3f2fd;
}
